import React from "react";
import { Card, Popover,ButtonGroup, Button } from "antd";
import {
    RetweetOutlined,
    HeartOutlined,
    MessageOutlined,
    EllipsisOutlied,
} from "@ant-design";

const PostCard = ({ post }) => {
    return (
        <div>
            <Card
                cover={post.Images[0] && <PostImages images={post.Images} />}
                actions={[
                    <RetweetOutlined key="retweet" />,
                    <HeartOutlined key="heart" />,
                    <MessageOutlined key="comment" />,
                    <Popover key="more" content={(
                      <ButtonGroup>
                        <Button>수정</Button>
                        <Button>수정</Button>
                      </ButtonGroup>
                    )}>
                        <EllipsisOutlied />
                    </Popover>,
                ]}
            >
                <Image />
                <Content />
                <Buttonss></Buttonss>
            </Card>
            <CommentForm />
            <Comments />
        </div>
    );
};

export default PostCard;
