import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
// import { AutoSizer, List } from 'react-virtualized'
import AppLayout from '../compoments/AppLayout';
import PostCard from '../compoments/PostCard';
import PostForm from '../compoments/postForm';
import { LOAD_POSTS_REQUEST } from '../reduers/post';

const Home = () => {
  const dispatch = useDispatch();
  const { me } = useSelector((state) => state.user);
  const { mainPosts, hasMorePosts, loadPostsLoading } = useSelector((state) => state.post);
  useEffect(() => {
    dispatch({
      type: LOAD_POSTS_REQUEST,
    });
  }, []);

  const [data, setData] = useState(mainPosts);
  const scrollListener = (params) => {
    if (params.scrollTop + params.clientHeight >= params.scrollHeight - 300) {
      if (data.length <= 100) {
        setData([
          ...data,
          mainPosts
        ]);
      }
    }
  };

  const rowRanderer = ({ index, style }) => {
    const post = data[index];
    return (
      <div style={style}>
        {post.content}
      </div>
    );
  };

  return (
    <AutoSizer AutoSizer>
      {({ width }) => (
        <List
          rowCount={data.length} // 항목의 개수
          height={400} // 실제 렌더링 되는 높이범위
          rowHeight={200} // 항목의높이
          width={width} // 항목의 너비
          rowRenderer={rowRanderer} // 항목렌더링할때쓰는 함수
          onScroll={scrollListener} // scroll 함수
          overscanRowCount={2} // 다음에 로드해올 항목 미리 컨텐츠 높이 잡기
        />
      )}
    </AutoSizer>
  );
};

