import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { AutoSizer, List } from 'react-virtualized';
import AppLayout from '../compoments/AppLayout';
import PostCard from '../compoments/PostCard';
import PostForm from '../compoments/postForm';
import { LOAD_POSTS_REQUEST } from '../reduers/post';

const Home = () => {
  const dispatch = useDispatch();
  const { me } = useSelector((state) => state.user);
  const [aaa,setaaa] = useState();
  const { mainPosts, hasMorePosts, loadPostsLoading } = useSelector((state) => state.post);
  useEffect(() => {
    dispatch({
      type: LOAD_POSTS_REQUEST,
    });
  }, []);

  useEffect(() => {
    function onScroll() {
      if (
        window.scrollY + document.documentElement.clientHeight
        > (document.documentElement.scrollHeight - 500)
      ) {
        if (hasMorePosts && !loadPostsLoading) {
          dispatch({
            type: LOAD_POSTS_REQUEST,
          });
        }
      }
    }
    setaaa(onScroll);
    window.addEventListener('scroll', onScroll);
    return () => {
      window.removeEventListener('scroll', onScroll);
    };
  }, [hasMorePosts, loadPostsLoading]);

  const rowRanderer = ({ index, style }) => {
    const post = mainPosts[index];
    return (
      <div style={style}>
        <PostCard key={post.id} post={post} />
      </div>
    );
  };

  return (
    <AutoSizer>

      <AppLayout>
        {({ width }) => (
          <List
            rowCount={mainPosts.length} // 항목의 개수
            height={400} // 실제 렌더링 되는 높이범위
            rowHeight={200} // 항목의높이
            width={width} // 항목의 너비
            rowRenderer={rowRanderer} // 항목렌더링할때쓰는 함수
            onScroll={scrollListener} // scroll 함수
            overscanRowCount={2}
          />
        )}
      </AppLayout>
    </AutoSizer>
  );
};

export default Home;
