import { all, fork, call, take, put } from "redux-saga/effects";
import axios from "axios";

function logInAPI(data) {
    return axios.post("/api/login", data);
}

/* 
const test = logIn({type: 'LOG_IN_REQUEST', data: {id: 'dkstn1230@naver.com'}})
test.next(); 
이렇게 테스트를 하기 편하다 yield를 사용하면
*/

// 로그인 할때 데이터도 같이 보내줘야 하는데 이럴땐
// 실행한 함수 안에 logIn함수 action 받아서

// call, fork 의 차이  fork는비동기, call은 동기 뭐가 다를까 call 를 하면 logInAPI가 리턴 할떄 까지 기다리면서 result 에 넣어주는데
// fork를 하면 기다리지 않고 그냥 보내버리고 바로 다음께 실행된다!!
function* logIn(action) {
    try {
        // 첫번째 자리가 함수고 그 다음부터는 매개변수들
        const result = yield call(logInAPI, action.data);
        // fork 를 사용하면 axios.post("/api/login");이거나 마찬가지 다
        // call 를 하면 axios.post("/api/login").then(() => {}) 이거랑 비슷하다
        // 정리하면 결과를 받아올떄 까지 기다리느냐 아니면 그냥 바로 넘어가느냐
        yield put({
            type: "LOG_IN_SUCCESS",
            data: result.data,
        });
    } catch (err) {
        yield put({
            type: "LOG_IN_FAILURE",
            data: err.response.data,
        });
    }
}

function logOutAPI() {
    return axios.post("/api/logout");
}

function* logOut() {
    try {
        const result = yield call(logOutAPI);

        yield put({
            type: "LOG_OUT_SUCCESS",
            data: result.data,
        });
    } catch (err) {
        yield put({
            type: "LOG_OUT_FAILURE",
            data: err.response.data,
        });
    }
}

function addPostAPI(data) {
    return axios.post("/api/addpost", data);
}

function* addPost(action) {
    try {
        const result = yield call(addPostAPI, action.data);

        yield put({
            type: "ADD_POST_SUCCESS",
            data: result.data,
        });
    } catch (err) {
        yield put({
            type: "ADD_POST_FAILURE",
            data: err.response.data,
        });
    }
}

// take 로그인
// 로그인이 실행 될떄 까지 기다리겠다
function* watchLogIn() {
    while(true){
        yield take("LOG_IN_REQUEST", logIn);
    }
}
function* watchLogOut() {
    while (true) {
        yield take("LOG_OUT_REQUEST", logOut);
    }
}
function* watchAddPost() {
    while (true) {
        
    }
    yield take("ADD_POST", addPost);
}

export default function* rootSaga() {
    yield all([
        fork(watchLogIn), //call 이랑 다름
        fork(watchLogOut),
        fork(watchAddPost),
    ]);
}
