import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { List } from 'react-virtualized';
import AppLayout from '../compoments/AppLayout';
import PostCard from '../compoments/PostCard';
import PostForm from '../compoments/postForm';
import { LOAD_POSTS_REQUEST } from '../reduers/post';

const Home = () => {
  const dispatch = useDispatch();
  const { me } = useSelector((state) => state.user);
  const { mainPosts, hasMorePosts, loadPostsLoading } = useSelector((state) => state.post);
  useEffect(() => {
    dispatch({
      type: LOAD_POSTS_REQUEST,
    });
  }, []);

  useEffect(() => {
    function onScroll() {
      if (
        window.scrollY + document.documentElement.clientHeight
        > (document.documentElement.scrollHeight - 500)
      ) {
        if (hasMorePosts && !loadPostsLoading) {
          dispatch({
            type: LOAD_POSTS_REQUEST,
          });
        }
      }
    }
    window.addEventListener('scroll', onScroll);
    return () => {
      window.removeEventListener('scroll', onScroll);
    };
  }, [hasMorePosts, loadPostsLoading]);
  return (
    <AppLayout>
      {me && <PostForm />}
      <List
        className="TodoList"
        width={512} // 전체크기
        height={513} // 전체 높이
        rowCount={todos.length} // 항목 개수
        rowHeight={57}
        rowRenderer={rowRenderer} // 항목을 렌더링할때 쓰는 함수
        list={todos}
        style={{ outline: 'none' }} // // List 에 기본 적용되는 outline 스타일 제거
      />
      {mainPosts.map((post) => (
        <PostCard key={post.id} post={post} />
      ))}
    </AppLayout>
  );
};

export default Home;
