import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import AppLayout from '../compoments/AppLayout';
import PostCard from '../compoments/PostCard';
import PostForm from '../compoments/postForm';
import { LOAD_POSTS_REQUEST } from '../reduers/post';

const Home = () => {
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch({
      type: LOAD_POSTS_REQUEST,
    });
  }, []);

  useEffect(() => {
    function onScroll() {
      console.log(
        window.scrollY,
        document.documentElement.clientHeight,
        document.documentElement.scrollHeight,
      );
      // eslint-disable-next-line max-len
      if (window.scrollY + document.documentElement.clientHeight === document.documentElement.scrollHeight) {
        dispatch({
          type: LOAD_POSTS_REQUEST,
        });
      }
    }
    window.addEventListener('onScroll', onScroll);
    return () => {
      window.removeEventListener('onScroll', onScroll);
    };
  }, []);

  return (
    <AppLayout>
      {me && <PostForm />}
      {mainPosts.map((post) => (
        <PostCard key={post.id} post={post} />
      ))}
    </AppLayout>
  );
};

export default Home;
