import React, { useCallback, useState } from "react";
import PropTypes from "prop-types";

const PostImages = ({ images }) => {
    const [showImagesZoome, setShowImagesZoome] = useState(false);

    const onZoom = useCallback(() => {
        setShowImagesZoome(true);
    }, []);

    if (images.length === 1) {
        return (
            <>
                <img
                    role="presentation"
                    src={images[0].src}
                    alt={images[0].src}
                    onClick={onZoom}
                />
            </>
        );
    }
    if (images.length === 2) {
        return (
            <>
                <img
                    role="presentation"
                    width="50%"
                    src={images[0].src}
                    alt={images[0].src}
                    onClick={onZoom}
                />
                <img
                    role="presentation"
                    width="50%"
                    src={images[1].src}
                    alt={images[1].src}
                    onClick={onZoom}
                />
            </>
        );
    }
    return (
        <>
            <div>
                <img
                    role="presentation"
                    width="50%"
                    src={images[0].src}
                    alt={images[0].src}
                    onClick={onZoom}
                />
                <div
                    role="presentation"
                    
                >

                </div>
            </div>
        </>
    );
};

PostImages.propTypes = {
    images: PropTypes.arrayOf(PropTypes.object),
};

export default PostImages;
