const intialState = {
    user: {
        isLoggedIn: false,
        user: null,
        signUpData: {},
        loginData: {},
    },
    post: {
        mainPosts: [],
    },
};
 
export const loginAction = (data) => {
    return {
        type: "LOG_IN",
        data,
    };
};
export const logoutAction = (data) => {
    return {
        type: "LOG_IN",
        data,
    };
};
// 비동기 액션 만들기

// 액션 만들기
const changeNickname = (data) => {
    return {
        type: "CHANGE_NICKNAME",
        data,
    };
};

changeNickname("에베베");

// (이전상태, 액션) => 다음상태
const rootRedicer = (state = intialState, action) => {
    switch (action.type) {
        case "LOG_IN":
            return {
                ...state,
                user: {
                    ...state.user,
                    isLoggedIn: true,
                    user: action.data,
                },
            };
    }
};

export default rootRedicer;
