import { HYDRATE } from "next-redux-wrapper";





const intialState = {
    user: {
    },
    post: {
    },
};

// 비동기 액션 만들기

// 액션 만들기
const changeNickname = (data) => {
    return {
        type: "CHANGE_NICKNAME",
        data,
    };
};

changeNickname("에베베");

// (이전상태, 액션) => 다음상태
const rootRedicer = (state = intialState, action) => {
    switch (action.type) {
        case HYDRATE:
            console.log("HYDRATE", action);
            return { ...state, ...action.payload };
        default:
            return state;
    }
};

export default rootRedicer;
